def rozdil(): seznam = [5, 6, 9, 11, 39] rozdil = seznam[0] - seznam[1] - seznam[2] - seznam[3] - seznam[4] print(f"Hromadný součet seznamu je {rozdil}")
def soucin(): seznam = [5, 6, 9, 11, 39] soucin = seznam[0] * seznam[1] * seznam[2] * seznam[3] * seznam[4] print(f"Hromadný součet seznamu je {soucin}")
def podil(): seznam = [5, 6, 9, 11, 39] podil = seznam[0] / seznam[1] / seznam[2] / seznam[3] / seznam[4] print(f"Hromadný součet seznamu je {podil}")
