def secist_cisla(cislo_1, cislo_2):
    secist = cislo_1 + cislo_2
    print(secist)


def odecist_cisla(cislo_1, cislo_2):
    odecist = cislo_1 - cislo_2
    print(odecist)

def vynasobit_cisla(cislo_1, cislo_2):
    vynasobit = cislo_1 * cislo_2
    print(vynasobit)

def vydelit_cisla(cislo_1, cislo_2):
  if cislo_1 == 0 or cislo_2 == 0:
    print("nedefinováno")
  else:
    vydelit = cislo_1 / cislo_2
    print(vydelit)

def hromadny_soucet(seznam_cisel):
  seznam_cisel = []
  while True:
    cislo = input("napis cislo nebo stop: ")
    if cislo.lower() == 'stop':
        break
    seznam_cisel.append(cislo)
    return sum(seznam_cisel)

def hromadne_odcitani(seznam_cisel):
  seznam_cisel = []
  while True:
    cislo1 = input("napis cislo nebo stop: ")
    if cislo1.lower() == 'stop':
        break
  result = seznam_cisel[0]
  for cislo in seznam_cisel[1:]:
      result = odecist_cisla(cislo_1, cislo_2)
  return result

def hromadne_vynasobeni(seznam_cisel):
  seznam_cisel = []
  while True:
    cislo1 = input("napis cislo nebo stop: ")
    if cislo1.lower() == 'stop':
        break
  result = seznam_cisel[0]
  for cislo in seznam_cisel[1:]:
      result = vynasobit_cisla(cislo_1, cislo_2)
  return result

def hromadne_deleni(seznam_cisel):
  seznam_cisel = []
  while True:
    cislo1 = input("napis cislo nebo stop: ")
    if cislo1.lower() == 'stop':
        break
  result = seznam_cisel[0]
  for cislo in seznam_cisel[1:]:
      result = vydelit_cisla(cislo_1, cislo_2)
  return result

