def doMath(n1, n2, operation):
    n1 = float(n1)
    n2 = float(n2)

    if operation == "+":
        return n1 + n2
    if operation == "-":
        return n1 - n2
    if operation == "*":
        return n1 * n2
    if operation == "/":
        if n2 == 0:
            return "nedefinováno"
        return n1 / n2

# seznam výsledků kalkulačky
results = []

# kalkulačka má parametr "result" označující, kolik výsledků kalkulačka vypočítala
def calculator(result):
    operation = input("Vyberte matematickou operaci (+, -, *, /): ")

    # seznam čísel pro matematickou opreaci
    numbers = []

    # kalkulačka se zeptá uživatele na čísla, dokud uživatel nezadá alespoň jedno číslo
    while len(numbers) < 1:
        _numbers = input("Zadejte čísla (oddělte čárkou): ")
        if _numbers != "":
            numbers = _numbers.split(",")

    # výsledek matematické operace
    number = 0

    # kalkulačka si za výsledek dosadí výsledek z poslední matematické operace,
    # který využije v případě, že uživatel zadal pouze jedno číslo
    if len(results) > 0:
        number = results[result - 1]

    # kalkulačka vypočítá rovnici s více čísly
    if len(numbers) > 1:
        # kalkulačka si za výsledek dosadí první číslo z matematické operace
        number = numbers[0]
        # kalkulačka provede matematickou operaci se všemi zadanými čísly
        for i in range(1, len(numbers)):
            number = doMath(number, numbers[i], operation)
    # kalkulačka vypočítá rovnici s jedním číslem
    else:
        # jako první číslo si dosadí výsledek z poslední matematické operace
        number = doMath(number, numbers[0], operation)

    print(f"Výsledek: {number}")

    results.append(number)

    calc = input("Chcete vypočítat další matematickou rovnici (Y/N)?")

    if calc.capitalize() == "Y":
        calculator(result + 1)
    else:
        print(results)

calculator(0)

