def secti(a, b):
    soucet = a + b
    return soucet

def rozdel(a, b):
    rozdil = a - b
    return rozdil

def vynasob(a, b):
    soucin = a * b
    return soucin

def vydel(a, b):
    if b != 0:
        podil = a / b
    else:
        podil = "Nedefinováno."
    return podil

