def deleni(x, y):
    try:
       solution = x / y
    except ZeroDivisionError:
        return "division by zero"
    else:
      return f"Answer is {solution}"

x = 6
y = 0

vysledek = deleni(x, y)
print(vysledek)

def scitani(cislo1= None, cislo2 = None):
   if cislo1 == None and cislo2 ==None:
      cislo1 = int(input("prosím zadejte libovolné číslo"))
      cislo2 = int(input("prosim zadejte libovolné číslo"))
      vysledek = cislo1 + cislo2
      print(vysledek)
   elif cislo1 == None:
        vysledek = cislo2
        print(vysledek)
   else:
        vysledek = cislo2 + cislo1
        print(vysledek)
