def scitani(x,y):
  return x + y

def odcitani(x,y):
  return x - y

def nasobeni(x,y):
  return x * y

def deleni(x,y):
  if y != 0:
    return x / y
  else:
    print("nedefinováno")

seznam1 = [9, 5, 7 ,6 ,3]
seznam2 = [5, 35, 9, 6, 8]

def hromadnescitani(seznam1,seznam2):
  return sum(seznam1) + sum(seznam2)

def hromadneodcitani(seznam1,seznam2):
  return sum(seznam1) - sum(seznam2)

def hromadnenasobeni(seznam1, seznam2):
  return sum(seznam1) * sum(seznam2)

def hromadnedeleni(seznam1,seznam2):
  if sum(seznam2) != 0:
    return sum(seznam1) / sum(seznam2)
  else:
    print("nedefinováno")

