    print("špatnej input")

def scitani(cislo_1, cislo_2):
  return cislo_1 + cislo_2
def odcitani(cislo_1, cislo_2):
  return cislo_1 - cislo_2
def nasobeni(cislo_1, cislo_2):
  return cislo_1 ** cislo_2
def deleni(cislo_1, cislo_2):
  if cislo_2 == 0:
    print("S touto číselnou hodnotou nelze počítat")
  else:
    return cislo_1 / cislo_2



