   cislo2 = float(input("Zadejte druhe cislo: "))

   if operator == '+':
    vysledek = cislo1 + cislo2
   elif operator == '-':
    vysledek = cislo1 - cislo2
   elif operator == '*':
    vysledek = cislo1 * cislo2
   elif operator == '/':
      if cislo2 != 0:
       vysledek = cislo1 / cislo2

      else:
       return "neplatny operator"
   else:
      return "nelze delit nulou"

return f"vysledek:" {cislo1} {operator} {cislo2} = {vysledek}
