        vysledek = cislo1 + cislo2
        vysledky.append(vysledek)

      if volba == 2:
        print("výsledek" ,cislo1, "-" ,cislo2, "=" ,minus(cislo1, cislo2))
        vysledek = cislo1 + cislo2
        vysledky.append(vysledek)

      if volba == 3:
        print("výsledek" ,cislo1, "x" ,cislo2, "=" ,krat(cislo1, cislo2))
        vysledek = cislo1 + cislo2
        vysledky.append(vysledek)

      if volba == 4:
        if cislo2 == 0:
          print("nedefinováno, nelze dělit nulou")
        else:
          print("výsledek" ,cislo1, "/" ,cislo2, "=" ,deleno(cislo1, cislo2))
          vysledek = cislo1 + cislo2
          vysledky.append(vysledek)

    if volba == 10:
      print("rozumím, ukončuji")
      print(vysledky)
      break

