    cislo1 = int(input("zadejte libovolne cislo"))
    cislo2 = int(input("zadejte libovolne cislo"))
    vysledeks = cislo1 + cislo2
    print (f"vysledek scitani je {vysledeks}")
def odcitani():
    cislo1 = int(input("zadejte libovolne cislo"))
    cislo2 = int(input("zadejte libovolne cislo"))
    vysledeko = cislo1 - cislo2
    print(f"vysledek odcitani je {vysledeko}")
def nasobeni():
    cislo1 = int(input("zadejte libovolne cislo"))
    cislo2 = int(input("zadejte libovolne cislo"))
    vysledekn = cislo1 * cislo2
    print(f"vysledek nasobeni je {vysledekn}")
def deleni():
    cislo1 = int(input("zadejte libovolne cislo"))
    cislo2 = int(input("zadejte libovolne cislo"))
    if cislo2 == 0:
        print ("nedefinovano")
    else:
        vysledekd = cislo1 / cislo2
        print(f"vysledek deleni je {vysledekd}")
