def soucet():
  a = float(input("zadej první číslo:"))
  b = float(input("zadej první číslo:"))
  c = a + b
  print(f"vysledek sčítání je: {c}" )
def odcitani():
  a = float(input("zadej první číslo:"))
  b = float(input("co od něj chceš odečíst?:"))
  c = a - b
  print(f"výsledek odčítání je: {c}")
def nasobeni():
  a = float(input("zadej první číslo:"))
  b = float(input("zadej druhé číslo:"))
  c = a * b
  print(f"výsledek násobení je: {c}")
def deleni():
  a = float(input("zadej čitatele:"))
  b = float(input("zadej jmenovatele:"))
  if b == 0:
    print("jsi kokot, dělení nulou nejde kryple")
  else: c = a / b
  print(f"vysledek dělení je:{c}")
