def secti(a=None, b=None):
    if a is None:
        a = previous_result
    if b is None:
        b = float(input("Zadejte druhé číslo: "))
    result = a + b
    results.append(result)
    return result

def odecti(a=None, b=None):
    if a is None:
        a = previous_result
    if b is None:
        b = float(input("Zadejte druhé číslo: "))
    result = a - b
    results.append(result)
    return result

def vynasob(a=None, b=None):
    if a is None:
        a = previous_result
    if b is None:
        b = float(input("Zadejte druhé číslo:"))
    result = a * b
    results.append(result)
    return result

def vydel(a=None, b=None):
    if a is None:
        a = previous_result
    if b is None:
        b = float(input("Zadejte druhé číslo: "))
    if b == 0:
        return "nedefinováno"
    result = a / b
    results.append(result)
    return result

def hromady_soucet(numbers):
    result = sum(numbers)
    results.append(result)
    return result

def hromadne_nasobeni(numbers):
    result = 1
    for num in numbers:
        result *= num
    results.append(result)
    return result

