  cilova_stanice = input("zadejte cílovou stanici:")
  if cilova_stanice == None:
    print("stanice neexistuje")
  elif 0<= cilova_stanice <= stanice(len(stanice)+1):
    konecna = stanice[cilova_stanice - 1]
  else:
    print("vlaky sem nejezdí")
    return cilova_stanice, konecna
