    print(f"Vítáme Vás {cestujici} ve vlaku {seznam_vlaku[1]}")
    print(f"Cílová stanice vlaku je: {vyber_cesty}")
  elif vyber_cesty == cilove_stanice[2]:
    print(f"Vítáme Vás {cestujici} ve vlaku {seznam_vlaku[2]}")
    print(f"Cílová stanice vlaku je: {vyber_cesty}")
  elif vyber_cesty == cilove_stanice[3]:
    print(f"Vítáme Vás {cestujici} ve vlaku {seznam_vlaku[3]}")
    print(f"Cílová stanice vlaku je: {vyber_cesty}")



