   return jmeno

def destinace():
   while (destinace != 'Praha') and (destinace != 'Trnovany') and (destinace != 'Paríž') and (destinace != 'Kralupy'):
     if (destinace != 'Praha') and (destinace != 'Trnovany') and (destinace != 'Paríž') and (destinace != 'Kralupy'):
       print("Omlouváme se, ale tam naše vlaky jestě nejezdí. Pracujeme na tom.")
     destinace = input(f'Dobrý den {jmeno}, kam dneska chcete jet? Vyberete, prosím, stanici, kterou máme na seznamu:\1 - Praha\2 - Trnovany\3 - Paříž\4 - Kralupy')
   return destinace

def hledej(destinace: str, seznam_vlaku: List, seznam_stanic: List, seznam_odjezdu: List):
  for i in range(len(seznam_stanic)):
    if seznam_stanic[i] --destinace:
      nazev_vlaku = seznam_vlaku(i)
      cas_odjezdu = seznam_odjezdu[i]
  return nazev_vlaku, cas_odjezdu

def privitani(jmeno: str, nazev_vlaku: str, destinace: str):
  print( f'Znovu dobrý den{jmeno}, ale tentokrát už ve vlaku{nazev_vlaku}. Dojedeme do {destinace} v {cas_odjezdu} ')
  return

