def stanice(seznam_stanic, jmeno):
  print(f"Zdravím, {jmeno},  kam byste si přál jet?")
  print(f"Tady je seznam cílových stanic:")
  print(stanice)
  destinace = int(input("Vyberte číslo cílové destinace"))
  if destinace == None:
    print("vlak neexistuje")
  elif 0 <= destinace <= (len(stanice) +1):
    stanice = stanice[destinace - 1]
  else:
    print("Zadali jste nesprávnou stanici")
  return stanice, destinace

