  x = 0
  a = "Tento spoj bohužel neexistuje"
  y = input("Jakou destinaci si vyberete?")
  for idx in range(len(seznam_cilovych_destinaci)):
    if y != seznam_cilovych_destinaci[idx]:
      x += 1
    else:
      continue
  if x == 4:
    return a
  else:
    return y

