def cestujici():
    jmeno = input("Zadej své jméno, cestovateli: ")
    return jmeno

def cilova_destinace(jmeno, seznam_destinaci):
    print("Zde je seznam všech stanic:")
    for item in seznam_destinaci:
        print(item)
    cilovastanice = input(f"Dobrý den {jmeno}, kam si přejete cestovat: ")
    return cilovastanice

def vlak(cilovastanice, seznam_vlaku, seznam_destinaci, seznam_odjezdu):
    if cilovastanice in seznam_destinaci:
        index_destinace = seznam_destinaci.index(cilovastanice)
        vlak = seznam_vlaku[index_destinace]
        odjezd = seznam_odjezdu[index_destinace]
        print(f"Vlak {vlak} vás dovezeme do {cilovastanice}. Odjíždí v {odjezd}.")
    else:
        print("Neplatná destinace.")

def vyber_mista():
    sedadlo = input("Vyberte si, kde chcete sedět (ulička, okno): ").lower()
    if sedadlo == "ulička":
        print(f"Dobrý den {jmeno}, děkujeme za výběr uličního sedadla.")
    elif sedadlo == "okno":
        print(f"Dobrý den {jmeno}, děkujeme za výběr sedadla u okna.")
    else:
        print("Neplatná volba.")

def nadrazi():
    seznam_vlaku = ["R606 - Krušnohor", "R23", "EC117 - Silesia", "RJ70 - Vindobona"]
    seznam_destinaci = ["Praha - Hlavní nádraží", "Litoměřice", "Berlin", "Vídeň"]
    seznam_odjezdu = ["16:45", "16:58", "17:16", "18:01"]

    jmeno = cestujici()
    cilovastanice = cilova_destinace(jmeno, seznam_destinaci)
    vlak(cilovastanice, seznam_vlaku, seznam_destinaci, seznam_odjezdu)
    vyber_mista()

nadrazi()

