    cil_input = input("Vyberte cíl (0 - 3): ")
    while cil_input == "" or int(cil_input) > 3:
        cil_input = input("Vyberte cíl (0 - 3): ")


def vlak(cil_input, vlaky, cily):
    global vlak_, cil, cas
    for i in range(len(vlaky)):
        if i == int(cil_input):
            vlak_ = vlaky[i]
            cil = cily[i]
            cas = casy[i]

def vyber_sedadla():
    global sedadlo
    print("Zde je seznam sedadel: ")
    for i in range(len(sedadla)):
        print(f"{i}. {sedadla[i]}")
    sedadlo_input = input("Vyberte sedadlo (0 - 2): ")

    while sedadlo_input == "" or int(sedadlo_input) > 2:
        sedadlo_input = input("Vyberte sedadlo (0 - 2): ")
    for i in range(len(sedadla)):
        if int(sedadlo_input) == i:
            sedadlo = sedadla[i]

