x = float(input("zadejte prvni cislo"))
y = float(input("zadejte druhe cislo"))


def scitani(x, y):
  vysledek = x + y
  print(vysledek)
def odcitani(x, y):
  vysledek = x - y
  print(vysledek)
def nasobeni(x, y):
  vysledek = x * y
  print(vysledek)
def deleni(x, y):
  vysledek = x / y
  if y == 0:
    print("neni definovano")
  else:
    print(vysledek)

print("vyber operaci")
print("1.scitani")
print("2.odcitani")
print("3.nasobeni")
print("4.deleni")

while True:
    choice = input("vyber (1/2/3/4):"),

    if choice in ('1', '2', '3', '4'):
        try:
            num1 = float(input("zadej prvni cislo: "))
            num2 = float(input("zadej druhe cislo: "))
        except ValueError:
            print("nevalidni vlozeni, vlozte cislo")
            continue

        if choice == '1':
            print(num1, "+", num2, "=", add(num1, num2))

        elif choice == '2':
            print(num1, "-", num2, "=", subtract(num1, num2))

        elif choice == '3':
            print(num1, "*", num2, "=", multiply(num1, num2))

        elif choice == '4':
            print(num1, "/", num2, "=", divide(num1, num2))

        next_calculation = input("chcete dale pocitat? (ano/ne): ")
        if next_calculation == "ne":
          break
    else:
        print("nevalidni vlozeni")

from typing import List
seznam_vlaku = ['Mašinka Tomáš','prdítko','kůň','motoráček']
seznam_stanic = ['Praha','Brno','Polepy','Břehorye']
seznam_odjezdu = ['10:10','12:12','14:14','16:16']

def jmeno():
  vase_jmeno = input('Zadej jméno kámo: ')
  return vase_jmeno

def destinacni_kalkulacka(meno: str):
  destinace = 'negr'
  while (destinace != 'Praha') and (destinace != 'Brno') and (destinace != 'Polepy') and (destinace != 'Břehorye'):
    if destinace == 'negr':
      pass
    elif (destinace != 'Praha') and (destinace != 'Brno') and (destinace != 'Polepy') and (destinace != 'Břehorye'):
      print('Tam nejedem')
    destinace = input(f'Dobrý den {meno}, kam byste si přál jet? Zde je seznam možných cílových stanic:\n1 - Praha\n2 - Brno\n3 - Polepy\n4 - Břehorye')
  return destinace

def hledej(destinace: str, seznam_vlaku: List, seznam_stanic: List, seznam_odjezdu: List):
  for i in range(len(seznam_stanic)):
    if seznam_stanic[i] == destinace:
      nazev_vlaku = seznam_vlaku[i]
      cas_odjezdu = seznam_odjezdu[i]
  return nazev_vlaku,cas_odjezdu

def rozsireni_listku():
  bonus = input('Chcete nějaký VIP bonus? ')
  if (bonus == 'ano') or (bonus == 'jo'):
    realnej_bonus = input('Co to bude? ')
  else:
    print('Tak jseš piča')
  return realnej_bonus

def privitani(meno: str, nazev_vlaku: str, destinace: str, cas_odjezdu, VIP):
  print(f'Vítáme Vás {meno} ve vlaku {nazev_vlaku} Cílová stanice {nazev_vlaku} je: {destinace} a odjezd je zanedlouho v {cas_odjezdu}, sami jste si vybrali jako VIP prvek {VIP}')
  return None



def main():
  stop = 'jdi se zabít'
  meno = jmeno()
  while stop != 'ne':
    destinace = destinacni_kalkulacka(meno)
    nazev_vlaku,cas_odjezdu = hledej(destinace,seznam_vlaku,seznam_stanic,seznam_odjezdu)
    VIP = rozsireni_listku()
    privitani(meno, nazev_vlaku, destinace, cas_odjezdu, VIP)
    stop = input('Chceš vybrat další vlak? ')

main()



