  cilova_stanice='abc'
  while (cilova_stanice!='litomerice') and (cilova_stanice!='lovosice') and (cilova_stanice!='sulejovice') and (cilova_stanice!='most'):
    if cilova_stanice=='abc':
      abcd=1234
