vlaky=["R606-Krušnohor","R23","EC117-Silesia","J70 - Vindobona"]
stanice=["Praha-Hlavní nádraží","Litoměřice","Berlín","Víděň"]
casy=["10:00","00:30","05:00","23:30"]
A=True
B=True
def cestujici():
  global jmeno
  while A==True:
    jmeno=input("Jak se jmenujete?")
    if jmeno=="":
      print("Určitě nějaké jméno máte, tak ho prosím zadejte: ")
      continue
    else:
      break
  return jmeno

def vyber_stanice(jmeno,stanice):
  B=True
  global cesta
  print(f"Dobrý den {jmeno}, kam byste si přál jet?")
  print("Zde je seznam všech cílových stanic:")
  for i in range(len(stanice)):
    print(stanice[i])
  while B==True:
    cesta=input()
    if cesta=="Praha-Hlavní nádraží"or "Litoměřice"or"Berlín"or"Víděň":
      B=False
    else:
      print("Žádná taková stanice není na výběr vyberte jednu z možností:")
  return cesta

def cilova_stanice(cesta,vlaky, stanice):
  for i in range(len(stanice)):
    if cesta==stanice[i]:
      print(f"Do stanice {cesta} se dostanete pomocí vlaku {vlaky[i]}")

def privitani(cesta, vlaky, stanice, jmeno):
  for i in range(len(stanice)):
    if cesta==stanice[i]:
      print(f"Vitame vás {jmeno}, ve vlaku {vlaky[i]}. Cílová stanice ve vlaku je: {stanice[i]}")

def casy_vlaku(vlaky,casy):
  print("Zde je čas odjezdů všech vlaků:")
  for i in range(len(vlaky)):
    print(f"Vlak {vlaky[i]} odjezd {casy[i]}")

def vyber_sedadla():
  while A==True:
    a=input("V jakém typu vozu chcete sedět(tichý, kuřácký, normální)?")
    if a=="tichý"or"kuřácký"or"normální":
      break
    else:
      print("Vaše odpověď není platná:")
      continue
  while A==True:
    b=input("Jaké sedadlo chcete(u okna, v uličce)?")
    if a=="u okna"or"v uličce":
      break
    else:
      print("Vaše odpověď není platná:")
      continue

while B==True:
  cestujici()
  vyber_stanice(jmeno,stanice)
  cilova_stanice(cesta,vlaky,stanice)
  privitani(cesta,vlaky,stanice,jmeno)
  casy_vlaku(vlaky,casy)
  vyber_sedadla()
  print("Děkujeme a teď další")
