def doMath(n1, n2, operation):
    n1 = float(n1)
    n2 = float(n2)

    if operation == "+":
        return n1 + n2
    if operation == "-":
        return n1 - n2
    if operation == "*":
        return n1 * n2
    if operation == "/":
        if n2 == 0:
            return "nedefinovÃ¡no"
        return n1 / n2

# seznam vÃ½sledkÅ¯ kalkulaÄky
results = []

# kalkulaÄka mÃ¡ parametr "result" oznaÄujÃ­cÃ­, kolik vÃ½sledkÅ¯ kalkulaÄka vypoÄÃ­tala
def calculator(result):
    operation = input("Vyberte matematickou operaci (+, -, *, /): ")

    # seznam ÄÃ­sel pro matematickou opreaci
    numbers = []

    # kalkulaÄka se zeptÃ¡ uÅ¾ivatele na ÄÃ­sla, dokud uÅ¾ivatel nezadÃ¡ alespoÅˆ jedno ÄÃ­slo
    while len(numbers) < 1:
        _numbers = input("Zadejte ÄÃ­sla (oddÄ›lte ÄÃ¡rkou): ")
        if _numbers != "":
            numbers = _numbers.split(",")

    # vÃ½sledek matematickÃ© operace
    number = 0

    # kalkulaÄka si za vÃ½sledek dosadÃ­ vÃ½sledek z poslednÃ­ matematickÃ© operace,
    # kterÃ½ vyuÅ¾ije v pÅ™Ã­padÄ›, Å¾e uÅ¾ivatel zadal pouze jedno ÄÃ­slo
    if len(results) > 0:
        number = results[result - 1]

    # kalkulaÄka vypoÄÃ­tÃ¡ rovnici s vÃ­ce ÄÃ­sly
    if len(numbers) > 1:
        # kalkulaÄka si za vÃ½sledek dosadÃ­ prvnÃ­ ÄÃ­slo z matematickÃ© operace
        number = numbers[0]
        # kalkulaÄka provede matematickou operaci se vÅ¡emi zadanÃ½mi ÄÃ­sly
        for i in range(1, len(numbers)):
            number = doMath(number, numbers[i], operation)
    # kalkulaÄka vypoÄÃ­tÃ¡ rovnici s jednÃ­m ÄÃ­slem
    else:
        # jako prvnÃ­ ÄÃ­slo si dosadÃ­ vÃ½sledek z poslednÃ­ matematickÃ© operace
        number = doMath(number, numbers[0], operation)

    print(f"VÃ½sledek: {number}")

    results.append(number)

    calc = input("Chcete vypoÄÃ­tat dalÅ¡Ã­ matematickou rovnici (Y/N)?")

    if calc.capitalize() == "Y":
        calculator(result + 1)
    else:
        print(results)

calculator(0)

