   if cislo1 == None and cislo2 ==None:
      cislo1 = int(input("prosÃ­m zadejte libovolnÃ© ÄÃ­slo"))
      cislo2 = int(input("prosim zadejte libovolnÃ© ÄÃ­slo"))
      vysledek = cislo1 + cislo2
      print(vysledek)
   elif cislo1 == None:
        vysledek = cislo2
        print(vysledek)
   else:
        vysledek = cislo2 + cislo1
        print(vysledek)
