trains = ["Tiger", "Spitfire", "Maus", "Gustav"]
stations = ["Praha (CZ)", "KokotÃ³w (PL)", "Kyjev (UA)", "Kunda (EE)"]
times = ["19:40", "19:41", "19:43", "19:45"]

def vlaky():
    name = enter_username();
    station = select_station(stations, name)
    train = get_train(station, trains, stations)

    train_welcome(name, station, train)
    departure(trains, train, times)
    seat()

def enter_username():
    return input("Zadejte svÃ© jmÃ©no: ")

def select_station(stations, name):
    text = \
        f"DobrÃ½ den {name}, kam byste si pÅ™Ã¡l jet?\n" \
        f"Zde je seznam moÅ¾nÃ½ch cÃ­lovÃ½ch stanic:\n"

    for i in range(0, 4):
        text += f"{i + 1}. {stations[i]}\n"

    station = input(text)

    while station != "1" and station != "2" and station != "3" and station != "4":
        station = input(text)

    return stations[int(station) - 1]

def get_train(station, trains, stations):
    i = stations.index(station)
    return trains[i]

def train_welcome(name, station, train):
    print(f"VÃ­tÃ¡me VÃ¡s {name} ve vlaku {train}. CÃ­lovÃ¡ stanice vlaku je: {station}.")

def departure(trains, train, times):
    i = trains.index(train);
    print(f"VÃ¡Å¡ vlak odjÃ­Å¾dÃ­ v {times[i]}.")

def seat():
    text = "Vyberte si sedadlo:\n" \
        "1. Okno\n" \
        "2. UliÄka\n" \
        "3. TichÃ½ vÅ¯z\n"

    seat = input(text)

    while seat != "1" and seat != "2" and seat != "3":
        seat = input(text)

    return seat

