def prevod_teplot():
  while True:
    celsium = float(input("Zadejte teplotu v celsiích: "))
    zvolte_jednotku = input("Zvolte funkci F (fahrenheit) nebo K (kelvin): ")
    if zvolte_jednotku != "F" or "K":
      print("Neplatná hodnota")
      break
    elif zvolte_jednotku == "F":
      f = celsium * 9/5 + 32.1
      return f
      print(f"Převod z {celsium} celsia je {f} fahrenheit.")
    else:
      k = celsium + 273.15
      return k
      print(f"Převod z {celsium} celsia je {k} kelvin.")


