def prevod_teplot(teploty, jednotka):
    for x in range(len(teploty)):
        c = teploty[x]
        if jednotka == 'F':
            teploty[x] = c * 9/5 + 32
        elif jednotka == 'K':
            teploty[x] = c + 273.15
        else:
            return 'Neplatná hodnota'
    return teploty

