import statistics
def analyza_pocasi(teploty, typ_vstupu):
  for teplota in teploty:
    if typ_vstupu == "max":
      teplota = max(teploty)
      return teplota
    elif typ_vstupu == "min":
      teplota = min(teploty)
      return teplota
    elif typ_vstupu == "prum":
      teplota = statistics.median(teploty)
      return teplota
    else:
     print("Neplatny typ vystupu")

