def prevod_teplot(seznam_teplot, jednotka):
  vysledne_teploty = []
  for teplota in range(len(seznam_teplot)):

    if jednotka == "F":
      prevod = teplota * 9/5 + 32.1
      vysledne_teploty.append(prevod)

    elif jednotka == "K":
      prevod = teplota + 273.15
      vysledne_teploty.append(prevod)

    else:
      vysledne_teploty = "Neplatny vstup"
  return vysledne_teploty

