def analyza_pocasi(teploty,typ_vstupu):
    cisla = []
    for hodnota in teploty:
      if typ_vstupu == "max":
        hodnota = max(teploty)
        cisla.append(hodnota)
      elif typ_vstupu == "min":
        hodnota = min(teploty)
        cisla.append(hodnota)
      elif typ_vstupu == "prum":
        soucet = sum(teploty)
        hotnota = soucet/7
        cisla.append(hodnota)
      else:
        print("Neplatný typ výstupu")
    return cisla

