def c(e:List[float],USD:float,EUR:float,GBP:float):
  d=input("Vyberte si měnu [USD,EUR,GBP]: ")
  if d=="USD":
    for idx in range(len(e)):
      e[idx]=(e[idx]*USD)
    return print(e)
  elif d=="EUR":
    for idx in range(len(e)):
      e[idx]=(e[idx]*EUR)
    return print(e)
  elif d=="GBP":
    for idx in range(len(e)):
      e[idx]=(e[idx]*GBP)
    return print(e)
  else:
    return print("Neplatná hodnota.")
