def analyza_pocasi(a, b):
  c = 0
  if b == "max":
    c = max(a)
  elif b == "min":
    c = min(a)
  elif b == "prum":
    c = sum(a) / len(a)
    c = round(c, 2)
  elif b != "max" or "min" or "prum":
    c = "neplatný výstup"
  return c
