def analyza_pocasi(teploty,typ_vystupu):
  if typ_vystupu == 'max':
   return max(teploty)
  elif typ_vystupu == 'min':
    return min(teploty)
  elif typ_vystupu == 'prum':
    return round(sum(teploty)/len(teploty),2)
  else:
    return 'Neplatný typ výstupu'

