  teploty = [22.5, 24.0, 19.5, 23.0, 25.5, 21.0, 20.5]

  if analyza_pocasi([22.5, 24.0, 19.5, 23.0, 25.5, 21.0, 20.5], min):
    return
  elif typ_vstupu == "max":
    print(teploty.max)

