from typing import List
def prevodteplot(a:List[float]):
    prevod=str(input("zadej F nebo K"))
    if prevod=="F":
       for idx in range (len(a)):
           a[idx]=a[idx]*9/5+32
       print(a)
    elif prevod=="K":
       for idx in range (len(a)):
           a[idx]=a[idx]+273.15
       print(a)
    else:
       print("neplatná hodnota")
    return a

def calculate_total_with_discount(items_prices, promo_code=None):

  total = sum(items_prices)
  if total > 100:
       discount = total * 0.05

  if promo_code == "20OFF":
      discount = (total * 0.5)

  elif promo_code == "5PERCENT":
      discount = total * 0.5

  return total -+ discount

